import React, { useState } from 'react';
import GlobalStyle from './styles/GlobalStyle';
import Header from './components/Header';
import Equipment from './components/Equipment';
import SelectedEquipment from './components/SelectedEquipment';
import Timeline from './components/Timeline';
import TrainingTips from './components/TrainingTips';

import { equipmentData } from './data/equipmentData';
import { timelineData } from './data/timelineData';
import { trainingTipsData } from './data/trainingTipsData';

function App() {
  const [selectedEquipment, setSelectedEquipment] = useState([]);

  const handleSelect = (item) => {
    setSelectedEquipment((prev) =>
      prev.find((i) => i.id === item.id) ? prev.filter((i) => i.id !== item.id) : [...prev, item]
    );
  };

  return (
    <>
      <GlobalStyle />
      <Header />
      <Equipment equipment={equipmentData} handleSelect={handleSelect} />
      <SelectedEquipment selectedItems={selectedEquipment} />
      <Timeline timeline={timelineData} />
      <TrainingTips tips={trainingTipsData} />
    </>
  );
}

export default App;
